const puppeteer = require('puppeteer');

const password = '***********'
const user = '************'


async function oprettelse(navn,simcardNumber,initialer,abonnoment,opretter) {
    const browser = await puppeteer.launch(
        {headless: false},
        {args: ['--incognito']}
    );
    const page = await browser.newPage();
    await page.goto('https://selvbetjening.tdc.dk/');
    await page.waitForSelector('#username');
    await page.type('#username', user);
    await page.type('#password', password);
    await sleep(2000);
    await page.click('#declineButton');
    await page.click('#post-button');
    const ink = await page.evaluate(() => {
        return document.querySelector('#ink').innerText;
    });
    console.log(ink);
    await browser.close();
}


function sleep(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
}

exports.oprettelse = oprettelse;